import React, { useState, useRef, useMemo } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import html2canvas from "html2canvas";
import Draggable from "react-draggable";

const featureColors = {
  parking: "#fef08a",
  staircase: "#f87171",
  garden: "#86efac",
  lift: "#a78bfa",
  pool: "#60a5fa",
  waterTank: "#38bdf8",
};

const formatLabel = (key) => {
  const result = key.replace(/([A-Z])/g, " $1");
  return result.charAt(0).toUpperCase() + result.slice(1) + " (ft)";
};

export default function PlotDiagramApp() {
  const [inputs, setInputs] = useState({
    plotWidth: 30,
    plotLength: 60,
    roadWidth: 30,
    setbackFront: 10,
    setbackBack: 5,
    setbackLeft: 5,
    setbackRight: 5,
    floors: 5,
  });

  const [features, setFeatures] = useState([]);
  const diagramRef = useRef(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInputs({ ...inputs, [name]: Math.max(0, parseFloat(value) || 0) });
  };

  const calculations = useMemo(() => {
    const { plotWidth, plotLength, setbackLeft, setbackRight, setbackFront, setbackBack, floors } = inputs;
    const plotArea = plotWidth * plotLength;
    const buildableWidth = Math.max(0, plotWidth - setbackLeft - setbackRight);
    const buildableLength = Math.max(0, plotLength - setbackFront - setbackBack);
    const groundFloorArea = buildableWidth * buildableLength;
    const totalBUA = groundFloorArea * floors;
    const groundCoverage = plotArea > 0 ? (groundFloorArea / plotArea) * 100 : 0;
    const far = plotArea > 0 ? totalBUA / plotArea : 0;
    return { plotArea, totalBUA, groundCoverage, far, buildableWidth, buildableLength };
  }, [inputs]);

  const handleExport = async () => {
    if (!diagramRef.current) return;
    const canvas = await html2canvas(diagramRef.current, { backgroundColor: null, logging: false });
    const link = document.createElement("a");
    link.download = "plot-diagram.png";
    link.href = canvas.toDataURL("image/png");
    link.click();
  };

  const addFeature = (type) => {
    setFeatures((prev) => [
      ...prev,
      {
        id: Date.now(),
        type,
        x: inputs.setbackLeft,
        y: inputs.setbackFront,
        width: type === "parking" ? 18 : 10,
        length: type === "parking" ? 18 : 10,
        rotation: 0,
      },
    ]);
  };

  const updateFeature = (id, updated) => {
    setFeatures((prev) => prev.map((f) => (f.id === id ? { ...f, ...updated } : f)));
  };

  const removeFeature = (id) => {
    setFeatures((prev) => prev.filter((f) => f.id !== id));
  };

  return (
    <div className="min-h-screen bg-gray-50 p-4 md:p-6 flex flex-col lg:flex-row gap-6 font-sans">
      <Card className="lg:w-1/3">
        <CardHeader>
          <CardTitle>Property Dimensions & Features</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {Object.keys(inputs).map((key) => (
            <div key={key} className="flex justify-between items-center text-sm">
              <label className="text-gray-600">{formatLabel(key)}</label>
              <input
                type="number"
                name={key}
                value={inputs[key]}
                onChange={handleChange}
                min="0"
                className="border p-1.5 w-28 text-right rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
          ))}

          <div className="border-t pt-4 mt-4 space-y-2">
            <h3 className="text-lg font-semibold mb-2">Calculated Metrics</h3>
            <div className="flex justify-between text-sm">
              <span>Plot Area:</span> <strong>{calculations.plotArea.toFixed(2)} sq.ft</strong>
            </div>
            <div className="flex justify-between text-sm">
              <span>Ground Coverage:</span> <strong>{calculations.groundCoverage.toFixed(2)} %</strong>
            </div>
            <div className="flex justify-between text-sm">
              <span>Total Built-Up Area (BUA):</span> <strong>{calculations.totalBUA.toFixed(2)} sq.ft</strong>
            </div>
            <div className="flex justify-between text-sm">
              <span>Floor Area Ratio (FAR):</span> <strong>{calculations.far.toFixed(2)}</strong>
            </div>
          </div>

          <div className="space-x-2 mt-4 flex flex-wrap">
            {["parking", "staircase", "garden", "lift", "pool", "waterTank"].map((type) => (
              <Button key={type} onClick={() => addFeature(type)}>
                Add {type.charAt(0).toUpperCase() + type.slice(1)}
              </Button>
            ))}
          </div>

          <Button onClick={handleExport} className="w-full mt-4">
            Export as PNG
          </Button>
        </CardContent>
      </Card>

      <Card className="flex-1 flex justify-center items-center p-4">
        <div ref={diagramRef} className="p-4 bg-white">
          <PlotDiagramSVG
            inputs={inputs}
            buildable={calculations}
            features={features}
            updateFeature={updateFeature}
            removeFeature={removeFeature}
          />
        </div>
      </Card>
    </div>
  );
}

const PlotDiagramSVG = ({ inputs, buildable, features, updateFeature, removeFeature }) => {
  const { plotWidth, plotLength, roadWidth, setbackFront, setbackBack, setbackLeft, setbackRight } = inputs;
  const PADDING = 20;
  const MAX_SIZE = 500;
  const totalWidthFt = plotWidth;
  const totalHeightFt = plotLength + roadWidth;

  if (totalWidthFt <= 0 || totalHeightFt <= 0) {
    return <div className="w-full h-full flex items-center justify-center bg-gray-100">Enter valid dimensions</div>;
  }

  const scale = Math.min(MAX_SIZE / totalWidthFt, MAX_SIZE / totalHeightFt);
  const svgWidth = totalWidthFt * scale + PADDING * 2;
  const svgHeight = totalHeightFt * scale + PADDING * 2;

  const pW = plotWidth * scale;
  const pL = plotLength * scale;
  const rW = roadWidth * scale;
  const sF = setbackFront * scale;
  const sB = setbackBack * scale;
  const sL = setbackLeft * scale;
  const sR = setbackRight * scale;
  const bW = buildable.buildableWidth * scale;
  const bL = buildable.buildableLength * scale;

  const handleResize = (e, f, corner) => {
    e.stopPropagation();
    const startX = e.clientX;
    const startY = e.clientY;
    const origWidth = f.width;
    const origLength = f.length;

    const onMouseMove = (ev) => {
      const dx = (ev.clientX - startX) / scale;
      const dy = (ev.clientY - startY) / scale;
      let newWidth = origWidth;
      let newLength = origLength;

      if (corner.includes("right")) newWidth = Math.max(1, origWidth + dx);
      if (corner.includes("bottom")) newLength = Math.max(1, origLength + dy);

      updateFeature(f.id, { width: newWidth, length: newLength });
    };

    const onMouseUp = () => {
      window.removeEventListener("mousemove", onMouseMove);
      window.removeEventListener("mouseup", onMouseUp);
    };

    window.addEventListener("mousemove", onMouseMove);
    window.addEventListener("mouseup", onMouseUp);
  };

  const handleRotate = (e, f) => {
    e.stopPropagation();
    const startX = e.clientX;
    const startY = e.clientY;
    const centerX = f.x * scale + (f.width * scale) / 2 + PADDING;
    const centerY = f.y * scale + (f.length * scale) / 2 + PADDING;
    const startAngle = f.rotation;

    const onMouseMove = (ev) => {
      const angle = Math.atan2(ev.clientY - centerY, ev.clientX - centerX);
      const startAngleRad = Math.atan2(startY - centerY, startX - centerX);
      const rotation = startAngle + ((angle - startAngleRad) * 180) / Math.PI;
      updateFeature(f.id, { rotation });
    };

    const onMouseUp = () => {
      window.removeEventListener("mousemove", onMouseMove);
      window.removeEventListener("mouseup", onMouseUp);
    };

    window.addEventListener("mousemove", onMouseMove);
    window.addEventListener("mouseup", onMouseUp);
  };

  return (
    <svg width={svgWidth} height={svgHeight} xmlns="http://www.w3.org/2000/svg">
      <g transform={`translate(${PADDING}, ${PADDING})`}>
        {/* Plot */}
        <rect x="0" y="0" width={pW} height={pL} fill="#a7f3d0" stroke="#15803d" strokeWidth="1" />

        {/* Road */}
        <rect x="0" y={pL} width={pW} height={rW} fill="#e5e7eb" stroke="#6b7280" strokeWidth="1" />
        <text x={pW / 2} y={pL + rW / 2} textAnchor="middle" alignmentBaseline="middle" style={{ fontSize: "10px", fill: "#374151", fontWeight: "bold" }}>
          Road ({roadWidth} ft)
        </text>

        {/* Buildable */}
        <rect x={sL} y={sB} width={bW} height={bL} fill="#bfdbfe" stroke="#3b82f6" strokeWidth="1" />
        <text x={sL + bW / 2} y={sB + bL / 2} textAnchor="middle" alignmentBaseline="middle" style={{ fontSize: "10px", fill: "#1e3a8a", fontWeight: "bold" }}>
          Buildable Area
        </text>

        {/* Dynamic Features */}
        {features.map((f) => {
          const x = f.x * scale;
          const y = f.y * scale;
          const w = f.width * scale;
          const h = f.length * scale;
          return (
            <Draggable
              key={f.id}
              bounds={{ left: sL, top: sB, right: sL + bW - w, bottom: sB + bL - h }}
              position={{ x, y }}
              onDrag={(e, data) => updateFeature(f.id, { x: data.x / scale, y: data.y / scale })}
            >
              <g transform={`rotate(${f.rotation}, ${w / 2}, ${h / 2})`} style={{ cursor: "move" }}>
                <rect width={w} height={h} fill={featureColors[f.type]} stroke="#000" strokeWidth="1" />
                <text x={w / 2} y={h / 2} textAnchor="middle" alignmentBaseline="middle" style={{ fontSize: "8px", fill: "#000", fontWeight: "bold" }}>
                  {f.type}
                </text>

                {/* Resize corner */}
                <rect
                  x={w - 4}
                  y={h - 4}
                  width={8}
                  height={8}
                  fill="#000"
                  style={{ cursor: "se-resize" }}
                  onMouseDown={(e) => handleResize(e, f, "bottom-right")}
                />

                {/* Rotate handle */}
                <circle
                  cx={w / 2}
                  cy={-10}
                  r={5}
                  fill="#f00"
                  style={{ cursor: "grab" }}
                  onMouseDown={(e) => handleRotate(e, f)}
                />

                {/* Remove button */}
                <rect
                  x={w - 10}
                  y={-10}
                  width={10}
                  height={10}
                  fill="#f00"
                  style={{ cursor: "pointer" }}
                  onClick={() => removeFeature(f.id)}
                />
              </g>
            </Draggable>
          );
        })}
      </g>
    </svg>
  );
};
